import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      // Only log response data in development to prevent sensitive data leaks
      if (capturedJsonResponse && process.env.NODE_ENV !== 'production') {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  // Production admin setup - ensure admin account exists on startup
  if (process.env.NODE_ENV === 'production' || process.env.REPLIT_DEPLOYMENT === '1') {
    try {
      console.log('ðŸ”§ Setting up production admin account...');
      const { exec } = await import('child_process');
      const { promisify } = await import('util');
      const execAsync = promisify(exec);

      await execAsync('node scripts/production-admin-setup.js');
      console.log('âœ… Production admin setup completed');
    } catch (error) {
      console.warn('âš ï¸  Production admin setup failed:', error instanceof Error ? error.message : String(error));
    }
  }

  const server = await registerRoutes(app);

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    // Custom static serving to avoid catch-all route interfering with API routes
    const path = await import("path");
    const fs = await import("fs");
    const distPath = path.resolve(import.meta.dirname, "..", "dist/public");

    if (!fs.existsSync(distPath)) {
      throw new Error(
        `Could not find the build directory: ${distPath}, make sure to build the client first`,
      );
    }

    // Serve static files
    app.use(express.static(distPath));

    // Only serve index.html for non-API routes
    app.use((req, res, next) => {
      // If this is an API route that wasn't matched, let it 404 properly
      if (req.path.startsWith('/api/')) {
        return res.status(404).json({ message: 'API endpoint not found' });
      }

      // For all other routes, serve index.html for client-side routing
      res.sendFile(path.resolve(distPath, "index.html"));
    });
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  // Error handler - must be at the very end of middleware stack
  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    // Log error in development
    if (process.env.NODE_ENV !== 'production') {
      console.error('Server error:', err);
    }

    res.status(status).json({ message });
    // Don't throw error after sending response - this prevents process crashes
  });

  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    email: claims["email"],
    password: "", // OAuth users don't need password
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    phone: "", // Default empty phone for OAuth users
    profileImageUrl: claims["profile_image_url"],
  }, claims["sub"]);
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user: any = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    // Pass the user object with session data
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./auth";
import { 
  insertServiceCategorySchema,
  insertServiceSchema,
  insertEventPromoSchema,
  insertBookingSchema,
  insertStaffSchema,
  insertStaffAvailabilitySchema,
  insertStaffServicesSchema,
  insertGalleryImageSchema,
  insertFeedbackSchema,
  insertContactMessageSchema,
  insertContentSettingsSchema,
} from "@shared/schema";
import { calculateEffectivePrice } from "../client/src/lib/pricing";
import {
  ObjectStorageService,
  ObjectNotFoundError,
} from "./objectStorage";
import { ObjectPermission } from "./objectAcl";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Note: Auth routes (/api/register, /api/login, /api/logout, /api/user) are now handled in auth.ts

  // Public routes - Service Categories
  app.get('/api/service-categories', async (req, res) => {
    try {
      const categories = await storage.getServiceCategories();
      res.json(categories);
    } catch (error) {
      console.error("Error fetching service categories:", error);
      res.status(500).json({ message: "Failed to fetch service categories" });
    }
  });

  // Public routes - Services
  app.get('/api/services', async (req, res) => {
    try {
      const services = await storage.getServices();
      const activePromos = await storage.getActiveEventPromos();

      // Calculate effective pricing for each service
      const servicesWithPricing = services.map(service => {
        const effectivePrice = calculateEffectivePrice(service, activePromos);
        return {
          ...service,
          effectivePrice: effectivePrice.final,
          originalPrice: effectivePrice.original,
          discount: effectivePrice.discount,
          hasDiscount: effectivePrice.hasDiscount,
        };
      });

      res.json(servicesWithPricing);
    } catch (error) {
      console.error("Error fetching services:", error);
      res.status(500).json({ message: "Failed to fetch services" });
    }
  });

  app.get('/api/services/:id', async (req, res) => {
    try {
      const service = await storage.getService(req.params.id);
      if (!service) {
        return res.status(404).json({ message: "Service not found" });
      }

      const activePromos = await storage.getActiveEventPromos();
      const effectivePrice = calculateEffectivePrice(service, activePromos);

      res.json({
        ...service,
        effectivePrice: effectivePrice.final,
        originalPrice: effectivePrice.original,
        discount: effectivePrice.discount,
        hasDiscount: effectivePrice.hasDiscount,
      });
    } catch (error) {
      console.error("Error fetching service:", error);
      res.status(500).json({ message: "Failed to fetch service" });
    }
  });

  // Public routes - Gallery
  app.get('/api/gallery', async (req, res) => {
    try {
      const { category } = req.query;
      const images = category 
        ? await storage.getGalleryImagesByCategory(category as string)
        : await storage.getGalleryImages();
      res.json(images);
    } catch (error) {
      console.error("Error fetching gallery:", error);
      res.status(500).json({ message: "Failed to fetch gallery" });
    }
  });

  // Public routes - Feedback (approved only)
  app.get('/api/feedback', async (req, res) => {
    try {
      const feedback = await storage.getApprovedFeedback();
      res.json(feedback);
    } catch (error) {
      console.error("Error fetching feedback:", error);
      res.status(500).json({ message: "Failed to fetch feedback" });
    }
  });

  // Public routes - Content Settings
  app.get('/api/content-settings', async (req, res) => {
    try {
      const settings = await storage.getContentSettings();
      res.json(settings);
    } catch (error) {
      console.error("Error fetching content settings:", error);
      res.status(500).json({ message: "Failed to fetch content settings" });
    }
  });

  // Public routes - Active Promotions
  app.get('/api/promotions', async (req, res) => {
    try {
      const promos = await storage.getActiveEventPromos();
      res.json(promos);
    } catch (error) {
      console.error("Error fetching promotions:", error);
      res.status(500).json({ message: "Failed to fetch promotions" });
    }
  });

  // Public routes - Staff
  app.get('/api/staff', async (req, res) => {
    try {
      const staff = await storage.getActiveStaff();
      res.json(staff);
    } catch (error) {
      console.error("Error fetching staff:", error);
      res.status(500).json({ message: "Failed to fetch staff" });
    }
  });

  // Public route - Staff by service
  app.get('/api/staff/by-service/:serviceId', async (req, res) => {
    try {
      const staff = await storage.getStaffByService(req.params.serviceId);
      res.json(staff);
    } catch (error) {
      console.error("Error fetching staff by service:", error);
      res.status(500).json({ message: "Failed to fetch staff by service" });
    }
  });

  // Public booking creation
  app.post('/api/bookings', async (req, res) => {
    try {
      const bookingData = insertBookingSchema.parse(req.body);
      const booking = await storage.createBooking(bookingData);

      // TODO: Send confirmation email (placeholder)
      console.log('Booking confirmation email would be sent to:', bookingData.customerEmail);

      res.status(201).json(booking);
    } catch (error) {
      console.error("Error creating booking:", error);
      res.status(500).json({ message: "Failed to create booking" });
    }
  });

  // Public feedback submission
  app.post('/api/feedback', async (req, res) => {
    try {
      const feedbackData = insertFeedbackSchema.parse(req.body);
      const feedback = await storage.createFeedback(feedbackData);
      res.status(201).json(feedback);
    } catch (error) {
      console.error("Error creating feedback:", error);
      res.status(500).json({ message: "Failed to create feedback" });
    }
  });

  // Public contact message submission
  app.post('/api/contact', async (req, res) => {
    try {
      const messageData = insertContactMessageSchema.parse(req.body);
      const message = await storage.createContactMessage(messageData);
      res.status(201).json(message);
    } catch (error) {
      console.error("Error creating contact message:", error);
      res.status(500).json({ message: "Failed to create contact message" });
    }
  });

  // Object Storage routes - Public file serving
  app.get("/public-objects/:filePath(*)", async (req, res) => {
    const filePath = req.params.filePath;
    const objectStorageService = new ObjectStorageService();
    try {
      const file = await objectStorageService.searchPublicObject(filePath);
      if (!file) {
        return res.status(404).json({ error: "File not found" });
      }
      objectStorageService.downloadObject(file, res);
    } catch (error) {
      console.error("Error searching for public object:", error);
      return res.status(500).json({ error: "Internal server error" });
    }
  });

  // Object Storage routes - Protected file serving (public objects with ACL)
  app.get("/objects/:objectPath(*)", async (req, res) => {
    const objectStorageService = new ObjectStorageService();
    try {
      const objectFile = await objectStorageService.getObjectEntityFile(req.path);
      // For gallery images, we want them to be publicly accessible for viewing
      // but only admin-uploadable, so we don't require authentication for read access
      objectStorageService.downloadObject(objectFile, res);
    } catch (error) {
      console.error("Error accessing object:", error);
      if (error instanceof ObjectNotFoundError) {
        return res.sendStatus(404);
      }
      return res.sendStatus(500);
    }
  });

  // Protected routes - Customer bookings
  app.get('/api/my-bookings', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const bookings = await storage.getBookingsByUser(userId);
      res.json(bookings);
    } catch (error) {
      console.error("Error fetching user bookings:", error);
      res.status(500).json({ message: "Failed to fetch bookings" });
    }
  });

  // Admin-only middleware
  const isAdmin = async (req: any, res: any, next: any) => {
    try {
      const userId = req.user.id;
      const user = await storage.getUser(userId);
      if (!user || user.role !== 'ADMIN') {
        return res.status(403).json({ message: "Admin access required" });
      }
      next();
    } catch (error) {
      res.status(500).json({ message: "Error checking admin status" });
    }
  };

  // Admin routes - Service Categories
  app.post('/api/admin/service-categories', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const categoryData = insertServiceCategorySchema.parse(req.body);
      const category = await storage.createServiceCategory(categoryData);
      res.status(201).json(category);
    } catch (error) {
      console.error("Error creating service category:", error);
      res.status(500).json({ message: "Failed to create service category" });
    }
  });

  app.put('/api/admin/service-categories/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const categoryData = insertServiceCategorySchema.partial().parse(req.body);
      const category = await storage.updateServiceCategory(req.params.id, categoryData);
      res.json(category);
    } catch (error) {
      console.error("Error updating service category:", error);
      res.status(500).json({ message: "Failed to update service category" });
    }
  });

  app.delete('/api/admin/service-categories/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      await storage.deleteServiceCategory(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting service category:", error);
      res.status(500).json({ message: "Failed to delete service category" });
    }
  });

  // Admin routes - Services
  app.post('/api/admin/services', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const serviceData = insertServiceSchema.parse(req.body);
      const service = await storage.createService(serviceData);
      res.status(201).json(service);
    } catch (error) {
      console.error("Error creating service:", error);
      res.status(500).json({ message: "Failed to create service" });
    }
  });

  app.put('/api/admin/services/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const serviceData = insertServiceSchema.partial().parse(req.body);
      const service = await storage.updateService(req.params.id, serviceData);
      res.json(service);
    } catch (error) {
      console.error("Error updating service:", error);
      res.status(500).json({ message: "Failed to update service" });
    }
  });

  app.delete('/api/admin/services/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      await storage.deleteService(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting service:", error);
      res.status(500).json({ message: "Failed to delete service" });
    }
  });

  // Admin routes - Event Promos
  app.get('/api/admin/event-promos', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const promos = await storage.getEventPromos();
      res.json(promos);
    } catch (error) {
      console.error("Error fetching event promos:", error);
      res.status(500).json({ message: "Failed to fetch event promos" });
    }
  });

  app.post('/api/admin/event-promos', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const promoData = insertEventPromoSchema.parse(req.body);
      const promo = await storage.createEventPromo(promoData);
      res.status(201).json(promo);
    } catch (error) {
      console.error("Error creating event promo:", error);
      res.status(500).json({ message: "Failed to create event promo" });
    }
  });

  app.put('/api/admin/event-promos/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const promoData = insertEventPromoSchema.partial().parse(req.body);
      const promo = await storage.updateEventPromo(req.params.id, promoData);
      res.json(promo);
    } catch (error) {
      console.error("Error updating event promo:", error);
      res.status(500).json({ message: "Failed to update event promo" });
    }
  });

  app.delete('/api/admin/event-promos/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      await storage.deleteEventPromo(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting event promo:", error);
      res.status(500).json({ message: "Failed to delete event promo" });
    }
  });

  // Admin routes - Bookings
  app.get('/api/admin/bookings', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const bookings = await storage.getBookings();
      res.json(bookings);
    } catch (error) {
      console.error("Error fetching bookings:", error);
      res.status(500).json({ message: "Failed to fetch bookings" });
    }
  });

  app.put('/api/admin/bookings/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const bookingData = insertBookingSchema.partial().parse(req.body);
      const booking = await storage.updateBooking(req.params.id, bookingData);
      res.json(booking);
    } catch (error) {
      console.error("Error updating booking:", error);
      res.status(500).json({ message: "Failed to update booking" });
    }
  });

  // Admin routes - Object Storage Upload
  app.post("/api/objects/upload", isAuthenticated, isAdmin, async (req, res) => {
    const objectStorageService = new ObjectStorageService();
    try {
      const uploadURL = await objectStorageService.getObjectEntityUploadURL();
      res.json({ uploadURL });
    } catch (error) {
      console.error("Error generating upload URL:", error);
      res.status(500).json({ error: "Failed to generate upload URL" });
    }
  });

  // Admin routes - Gallery Image Upload (set ACL after upload)
  app.put("/api/admin/gallery-upload", isAuthenticated, isAdmin, async (req: any, res) => {
    if (!req.body.imageURL || !req.body.category || !req.body.caption) {
      return res.status(400).json({ error: "imageURL, category, and caption are required" });
    }

    const userId = req.user.id;

    try {
      const objectStorageService = new ObjectStorageService();
      const objectPath = await objectStorageService.trySetObjectEntityAclPolicy(
        req.body.imageURL,
        {
          owner: userId,
          visibility: "public", // Gallery images should be publicly viewable
        }
      );

      // Create database record with the normalized object path
      const imageData = insertGalleryImageSchema.parse({
        url: objectPath,
        category: req.body.category,
        caption: req.body.caption,
        order: req.body.order || 0
      });

      const image = await storage.createGalleryImage(imageData);
      res.status(200).json({
        objectPath: objectPath,
        image: image
      });
    } catch (error) {
      console.error("Error setting gallery image:", error);
      res.status(500).json({ error: "Failed to save gallery image" });
    }
  });

  // Admin routes - Gallery
  app.post('/api/admin/gallery', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const imageData = insertGalleryImageSchema.parse(req.body);
      const image = await storage.createGalleryImage(imageData);
      res.status(201).json(image);
    } catch (error) {
      console.error("Error creating gallery image:", error);
      res.status(500).json({ message: "Failed to create gallery image" });
    }
  });

  app.put('/api/admin/gallery/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const imageData = insertGalleryImageSchema.partial().parse(req.body);
      const image = await storage.updateGalleryImage(req.params.id, imageData);
      res.json(image);
    } catch (error) {
      console.error("Error updating gallery image:", error);
      res.status(500).json({ message: "Failed to update gallery image" });
    }
  });

  app.delete('/api/admin/gallery/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      await storage.deleteGalleryImage(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting gallery image:", error);
      res.status(500).json({ message: "Failed to delete gallery image" });
    }
  });

  // Admin routes - Feedback Management
  app.get('/api/admin/feedback', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const feedback = await storage.getFeedback();
      res.json(feedback);
    } catch (error) {
      console.error("Error fetching feedback:", error);
      res.status(500).json({ message: "Failed to fetch feedback" });
    }
  });

  app.get('/api/admin/feedback/pending', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const feedback = await storage.getPendingFeedback();
      res.json(feedback);
    } catch (error) {
      console.error("Error fetching pending feedback:", error);
      res.status(500).json({ message: "Failed to fetch pending feedback" });
    }
  });

  // Admin Contact Messages routes
  app.get('/api/admin/contact-messages', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const contactMessages = await storage.getContactMessages();
      res.json(contactMessages);
    } catch (error) {
      console.error("Error fetching contact messages:", error);
      res.status(500).json({ message: "Failed to fetch contact messages" });
    }
  });

  app.put('/api/admin/feedback/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const feedbackData = insertFeedbackSchema.partial().parse(req.body);
      const feedback = await storage.updateFeedback(req.params.id, feedbackData);
      res.json(feedback);
    } catch (error) {
      console.error("Error updating feedback:", error);
      res.status(500).json({ message: "Failed to update feedback" });
    }
  });

  app.delete('/api/admin/feedback/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      await storage.deleteFeedback(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting feedback:", error);
      res.status(500).json({ message: "Failed to delete feedback" });
    }
  });

  // Admin routes - Contact Messages
  app.get('/api/admin/contact-messages', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const messages = await storage.getContactMessages();
      res.json(messages);
    } catch (error) {
      console.error("Error fetching contact messages:", error);
      res.status(500).json({ message: "Failed to fetch contact messages" });
    }
  });

  app.put('/api/admin/contact-messages/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const messageData = insertContactMessageSchema.partial().parse(req.body);
      const message = await storage.updateContactMessage(req.params.id, messageData);
      res.json(message);
    } catch (error) {
      console.error("Error updating contact message:", error);
      res.status(500).json({ message: "Failed to update contact message" });
    }
  });

  // Admin routes - Content Settings
  app.put('/api/admin/content-settings', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const settingsData = insertContentSettingsSchema.parse(req.body);
      const settings = await storage.updateContentSettings(settingsData);
      res.json(settings);
    } catch (error) {
      console.error("Error updating content settings:", error);
      res.status(500).json({ message: "Failed to update content settings" });
    }
  });

  // Admin routes - Staff Management
  app.get('/api/admin/staff', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const staff = await storage.getStaff();
      res.json(staff);
    } catch (error) {
      console.error("Error fetching staff:", error);
      res.status(500).json({ message: "Failed to fetch staff" });
    }
  });

  app.get('/api/admin/staff/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const staffMember = await storage.getStaffMember(req.params.id);
      if (!staffMember) {
        return res.status(404).json({ message: "Staff member not found" });
      }
      res.json(staffMember);
    } catch (error) {
      console.error("Error fetching staff member:", error);
      res.status(500).json({ message: "Failed to fetch staff member" });
    }
  });

  app.post('/api/admin/staff', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const staffData = insertStaffSchema.parse(req.body);
      const staff = await storage.createStaff(staffData);
      res.status(201).json(staff);
    } catch (error) {
      console.error("Error creating staff:", error);
      res.status(500).json({ message: "Failed to create staff" });
    }
  });

  app.put('/api/admin/staff/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const staffData = insertStaffSchema.partial().parse(req.body);
      const staff = await storage.updateStaff(req.params.id, staffData);
      res.json(staff);
    } catch (error) {
      console.error("Error updating staff:", error);
      res.status(500).json({ message: "Failed to update staff" });
    }
  });

  app.delete('/api/admin/staff/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      await storage.deleteStaff(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting staff:", error);
      res.status(500).json({ message: "Failed to delete staff" });
    }
  });

  // Admin routes - Staff Availability
  app.get('/api/admin/staff/:staffId/availability', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const availability = await storage.getStaffAvailability(req.params.staffId);
      res.json(availability);
    } catch (error) {
      console.error("Error fetching staff availability:", error);
      res.status(500).json({ message: "Failed to fetch staff availability" });
    }
  });

  app.post('/api/admin/staff/availability', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const availabilityData = insertStaffAvailabilitySchema.parse(req.body);
      const availability = await storage.createStaffAvailability(availabilityData);
      res.status(201).json(availability);
    } catch (error) {
      console.error("Error creating staff availability:", error);
      res.status(500).json({ message: "Failed to create staff availability" });
    }
  });

  app.put('/api/admin/staff/availability/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const availabilityData = insertStaffAvailabilitySchema.partial().parse(req.body);
      const availability = await storage.updateStaffAvailability(req.params.id, availabilityData);
      res.json(availability);
    } catch (error) {
      console.error("Error updating staff availability:", error);
      res.status(500).json({ message: "Failed to update staff availability" });
    }
  });

  app.delete('/api/admin/staff/availability/:id', isAuthenticated, isAdmin, async (req, res) => {
    try {
      await storage.deleteStaffAvailability(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting staff availability:", error);
      res.status(500).json({ message: "Failed to delete staff availability" });
    }
  });

  // Admin routes - Staff Services
  app.get('/api/admin/staff/:staffId/services', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const services = await storage.getStaffServices(req.params.staffId);
      res.json(services);
    } catch (error) {
      console.error("Error fetching staff services:", error);
      res.status(500).json({ message: "Failed to fetch staff services" });
    }
  });

  app.post('/api/admin/staff/:staffId/services/:serviceId', isAuthenticated, isAdmin, async (req, res) => {
    try {
      const staffService = await storage.assignServiceToStaff(req.params.staffId, req.params.serviceId);
      res.status(201).json(staffService);
    } catch (error) {
      console.error("Error assigning service to staff:", error);
      res.status(500).json({ message: "Failed to assign service to staff" });
    }
  });

  app.delete('/api/admin/staff/:staffId/services/:serviceId', isAuthenticated, isAdmin, async (req, res) => {
    try {
      await storage.removeServiceFromStaff(req.params.staffId, req.params.serviceId);
      res.status(204).send();
    } catch (error) {
      console.error("Error removing service from staff:", error);
      res.status(500).json({ message: "Failed to remove service from staff" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  serviceCategories,
  services,
  eventPromos,
  bookings,
  staff,
  staffAvailability,
  staffServices,
  galleryImages,
  feedback,
  contactMessages,
  contentSettings,
  type User,
  type UpsertUser,
  type ServiceCategory,
  type Service,
  type EventPromo,
  type Booking,
  type Staff,
  type StaffAvailability,
  type StaffServices,
  type GalleryImage,
  type Feedback,
  type ContactMessage,
  type ContentSettings,
  type InsertServiceCategory,
  type InsertService,
  type InsertEventPromo,
  type InsertBooking,
  type InsertStaff,
  type InsertStaffAvailability,
  type InsertStaffServices,
  type InsertGalleryImage,
  type InsertFeedback,
  type InsertContactMessage,
  type InsertContentSettings,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, gte, lte, sql } from "drizzle-orm";

// Interface for storage operations
export interface IStorage {
  // User operations (IMPORTANT) these user operations are mandatory for Auth.
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: UpsertUser): Promise<User>;
  upsertUser(user: UpsertUser, id?: string): Promise<User>;

  // Service Category operations
  getServiceCategories(): Promise<ServiceCategory[]>;
  createServiceCategory(category: InsertServiceCategory): Promise<ServiceCategory>;
  updateServiceCategory(id: string, category: Partial<InsertServiceCategory>): Promise<ServiceCategory>;
  deleteServiceCategory(id: string): Promise<void>;

  // Service operations
  getServices(): Promise<Service[]>;
  getService(id: string): Promise<Service | undefined>;
  createService(service: InsertService): Promise<Service>;
  updateService(id: string, service: Partial<InsertService>): Promise<Service>;
  deleteService(id: string): Promise<void>;

  // Event/Promo operations
  getActiveEventPromos(): Promise<EventPromo[]>;
  getEventPromos(): Promise<EventPromo[]>;
  createEventPromo(promo: InsertEventPromo): Promise<EventPromo>;
  updateEventPromo(id: string, promo: Partial<InsertEventPromo>): Promise<EventPromo>;
  deleteEventPromo(id: string): Promise<void>;

  // Booking operations
  getBookings(): Promise<(Booking & { service: Service, user?: User, staff?: Staff })[]>;
  getBookingsByUser(userId: string): Promise<(Booking & { service: Service, staff?: Staff })[]>;
  getBooking(id: string): Promise<(Booking & { service: Service, user?: User, staff?: Staff }) | undefined>;
  createBooking(booking: InsertBooking): Promise<Booking>;
  updateBooking(id: string, booking: Partial<InsertBooking>): Promise<Booking>;
  deleteBooking(id: string): Promise<void>;

  // Staff operations
  getStaff(): Promise<(Staff & { availability: StaffAvailability[], services: (StaffServices & { service: Service })[] })[]>;
  getActiveStaff(): Promise<(Staff & { availability: StaffAvailability[], services: (StaffServices & { service: Service })[] })[]>;
  getStaffByService(serviceId: string): Promise<(Staff & { availability: StaffAvailability[] })[]>;
  getStaffMember(id: string): Promise<(Staff & { availability: StaffAvailability[], services: (StaffServices & { service: Service })[] }) | undefined>;
  createStaff(staff: InsertStaff): Promise<Staff>;
  updateStaff(id: string, staff: Partial<InsertStaff>): Promise<Staff>;
  deleteStaff(id: string): Promise<void>;

  // Staff Availability operations
  getStaffAvailability(staffId: string): Promise<StaffAvailability[]>;
  createStaffAvailability(availability: InsertStaffAvailability): Promise<StaffAvailability>;
  updateStaffAvailability(id: string, availability: Partial<InsertStaffAvailability>): Promise<StaffAvailability>;
  deleteStaffAvailability(id: string): Promise<void>;

  // Staff Services operations
  getStaffServices(staffId: string): Promise<(StaffServices & { service: Service })[]>;
  createStaffService(staffService: InsertStaffServices): Promise<StaffServices>;
  deleteStaffService(id: string): Promise<void>;
  assignServiceToStaff(staffId: string, serviceId: string): Promise<StaffServices>;
  removeServiceFromStaff(staffId: string, serviceId: string): Promise<void>;

  // Gallery operations
  getGalleryImages(): Promise<GalleryImage[]>;
  getGalleryImagesByCategory(category: string): Promise<GalleryImage[]>;
  createGalleryImage(image: InsertGalleryImage): Promise<GalleryImage>;
  updateGalleryImage(id: string, image: Partial<InsertGalleryImage>): Promise<GalleryImage>;
  deleteGalleryImage(id: string): Promise<void>;

  // Feedback operations
  getFeedback(): Promise<(Feedback & { user?: User })[]>;
  getApprovedFeedback(): Promise<(Feedback & { user?: User })[]>;
  getPendingFeedback(): Promise<(Feedback & { user?: User })[]>;
  createFeedback(feedback: InsertFeedback): Promise<Feedback>;
  updateFeedback(id: string, feedback: Partial<InsertFeedback>): Promise<Feedback>;
  deleteFeedback(id: string): Promise<void>;

  // Contact operations
  getContactMessages(): Promise<ContactMessage[]>;
  createContactMessage(message: InsertContactMessage): Promise<ContactMessage>;
  updateContactMessage(id: string, message: Partial<InsertContactMessage>): Promise<ContactMessage>;
  deleteContactMessage(id: string): Promise<void>;

  // Content Settings operations
  getContentSettings(): Promise<ContentSettings | undefined>;
  updateContentSettings(settings: InsertContentSettings): Promise<ContentSettings>;
}

export class DatabaseStorage implements IStorage {
  // User operations (IMPORTANT) these user operations are mandatory for Replit Auth.
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async createUser(userData: UpsertUser): Promise<User> {
    const [user] = await db.insert(users).values(userData).returning();
    return user;
  }

  async upsertUser(userData: UpsertUser, id?: string): Promise<User> {
    const userWithId = id ? { ...userData, id } : userData;
    const [user] = await db
      .insert(users)
      .values(userWithId)
      .onConflictDoUpdate({
        target: users.email,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Service Category operations
  async getServiceCategories(): Promise<ServiceCategory[]> {
    return await db.select().from(serviceCategories).orderBy(serviceCategories.order);
  }

  async createServiceCategory(category: InsertServiceCategory): Promise<ServiceCategory> {
    const [created] = await db.insert(serviceCategories).values(category).returning();
    return created;
  }

  async updateServiceCategory(id: string, category: Partial<InsertServiceCategory>): Promise<ServiceCategory> {
    const [updated] = await db
      .update(serviceCategories)
      .set(category)
      .where(eq(serviceCategories.id, id))
      .returning();
    return updated;
  }

  async deleteServiceCategory(id: string): Promise<void> {
    await db.delete(serviceCategories).where(eq(serviceCategories.id, id));
  }

  // Service operations
  async getServices(): Promise<Service[]> {
    return await db.select().from(services);
  }


  async getService(id: string): Promise<Service | undefined> {
    const [result] = await db
      .select()
      .from(services)
      .where(eq(services.id, id));

    return result;
  }

  async createService(service: InsertService): Promise<Service> {
    // Ensure slug is unique by checking existing services
    let uniqueSlug = service.slug;
    let counter = 1;

    while (true) {
      const existing = await db
        .select({ id: services.id })
        .from(services)
        .where(eq(services.slug, uniqueSlug))
        .limit(1);

      if (existing.length === 0) {
        break; // Slug is unique, we can use it
      }

      // Slug exists, append counter and try again
      uniqueSlug = `${service.slug}-${counter}`;
      counter++;
    }

    const [created] = await db.insert(services).values({
      ...service,
      slug: uniqueSlug
    }).returning();
    return created;
  }

  async updateService(id: string, service: Partial<InsertService>): Promise<Service> {
    const [updated] = await db
      .update(services)
      .set(service)
      .where(eq(services.id, id))
      .returning();
    return updated;
  }

  async deleteService(id: string): Promise<void> {
    await db.delete(services).where(eq(services.id, id));
  }

  // Event/Promo operations
  async getActiveEventPromos(): Promise<EventPromo[]> {
    const now = new Date();
    return await db
      .select()
      .from(eventPromos)
      .where(
        and(
          eq(eventPromos.isActive, true),
          lte(eventPromos.startAt, now),
          gte(eventPromos.endAt, now)
        )
      );
  }

  async getEventPromos(): Promise<EventPromo[]> {
    return await db.select().from(eventPromos).orderBy(desc(eventPromos.createdAt));
  }

  async createEventPromo(promo: InsertEventPromo): Promise<EventPromo> {
    const [created] = await db.insert(eventPromos).values(promo).returning();
    return created;
  }

  async updateEventPromo(id: string, promo: Partial<InsertEventPromo>): Promise<EventPromo> {
    const [updated] = await db
      .update(eventPromos)
      .set(promo)
      .where(eq(eventPromos.id, id))
      .returning();
    return updated;
  }

  async deleteEventPromo(id: string): Promise<void> {
    await db.delete(eventPromos).where(eq(eventPromos.id, id));
  }

  // Booking operations
  async getBookings(): Promise<(Booking & { service: Service, user?: User, staff?: Staff })[]> {
    const results = await db
      .select()
      .from(bookings)
      .leftJoin(services, eq(bookings.serviceId, services.id))
      .leftJoin(users, eq(bookings.userId, users.id))
      .leftJoin(staff, eq(bookings.staffId, staff.id))
      .orderBy(desc(bookings.dateTime));

    return results.map(row => ({
      ...row.bookings,
      service: row.services!,
      user: row.users || undefined,
      staff: row.staff || undefined,
    }));
  }

  async getBookingsByUser(userId: string): Promise<(Booking & { service: Service, staff?: Staff })[]> {
    const results = await db
      .select()
      .from(bookings)
      .leftJoin(services, eq(bookings.serviceId, services.id))
      .leftJoin(staff, eq(bookings.staffId, staff.id))
      .where(eq(bookings.userId, userId))
      .orderBy(desc(bookings.dateTime));

    return results.map(row => ({
      ...row.bookings,
      service: row.services!,
      staff: row.staff || undefined,
    }));
  }

  async getBooking(id: string): Promise<(Booking & { service: Service, user?: User, staff?: Staff }) | undefined> {
    const [result] = await db
      .select()
      .from(bookings)
      .leftJoin(services, eq(bookings.serviceId, services.id))
      .leftJoin(users, eq(bookings.userId, users.id))
      .leftJoin(staff, eq(bookings.staffId, staff.id))
      .where(eq(bookings.id, id));

    if (!result) return undefined;

    return {
      ...result.bookings,
      service: result.services!,
      user: result.users || undefined,
      staff: result.staff || undefined,
    };
  }

  async createBooking(booking: InsertBooking): Promise<Booking> {
    const [created] = await db.insert(bookings).values(booking).returning();
    return created;
  }

  async updateBooking(id: string, booking: Partial<InsertBooking>): Promise<Booking> {
    const [updated] = await db
      .update(bookings)
      .set(booking)
      .where(eq(bookings.id, id))
      .returning();
    return updated;
  }

  async deleteBooking(id: string): Promise<void> {
    await db.delete(bookings).where(eq(bookings.id, id));
  }

  // Staff operations
  async getStaff(): Promise<(Staff & { availability: StaffAvailability[], services: (StaffServices & { service: Service })[] })[]> {
    const staffData = await db.select().from(staff).orderBy(staff.name);

    const enrichedStaff = await Promise.all(
      staffData.map(async (staffMember) => {
        const availability = await this.getStaffAvailability(staffMember.id);
        const services = await this.getStaffServices(staffMember.id);
        return { ...staffMember, availability, services };
      })
    );

    return enrichedStaff;
  }

  async getActiveStaff(): Promise<(Staff & { availability: StaffAvailability[], services: (StaffServices & { service: Service })[] })[]> {
    const staffData = await db.select().from(staff).where(eq(staff.isActive, true)).orderBy(staff.name);

    const enrichedStaff = await Promise.all(
      staffData.map(async (staffMember) => {
        const availability = await this.getStaffAvailability(staffMember.id);
        const services = await this.getStaffServices(staffMember.id);
        return { ...staffMember, availability, services };
      })
    );

    return enrichedStaff;
  }

  async getStaffByService(serviceId: string): Promise<(Staff & { availability: StaffAvailability[] })[]> {
    const staffServiceData = await db
      .select()
      .from(staffServices)
      .leftJoin(staff, eq(staffServices.staffId, staff.id))
      .where(eq(staffServices.serviceId, serviceId));

    const enrichedStaff = await Promise.all(
      staffServiceData.map(async (row) => {
        if (!row.staff) return null;
        const availability = await this.getStaffAvailability(row.staff.id);
        return { ...row.staff, availability };
      })
    );

    return enrichedStaff.filter(Boolean) as (Staff & { availability: StaffAvailability[] })[];
  }

  async getStaffMember(id: string): Promise<(Staff & { availability: StaffAvailability[], services: (StaffServices & { service: Service })[] }) | undefined> {
    const [staffMember] = await db.select().from(staff).where(eq(staff.id, id));
    if (!staffMember) return undefined;

    const availability = await this.getStaffAvailability(id);
    const services = await this.getStaffServices(id);
    return { ...staffMember, availability, services };
  }

  async createStaff(staffData: InsertStaff): Promise<Staff> {
    const [created] = await db.insert(staff).values(staffData).returning();
    return created;
  }

  async updateStaff(id: string, staffData: Partial<InsertStaff>): Promise<Staff> {
    const [updated] = await db
      .update(staff)
      .set(staffData)
      .where(eq(staff.id, id))
      .returning();
    return updated;
  }

  async deleteStaff(id: string): Promise<void> {
    // Delete related availability and services first
    await db.delete(staffAvailability).where(eq(staffAvailability.staffId, id));
    await db.delete(staffServices).where(eq(staffServices.staffId, id));
    await db.delete(staff).where(eq(staff.id, id));
  }

  // Staff Availability operations
  async getStaffAvailability(staffId: string): Promise<StaffAvailability[]> {
    return await db
      .select()
      .from(staffAvailability)
      .where(eq(staffAvailability.staffId, staffId))
      .orderBy(staffAvailability.dayOfWeek);
  }

  async createStaffAvailability(availability: InsertStaffAvailability): Promise<StaffAvailability> {
    const [created] = await db.insert(staffAvailability).values(availability).returning();
    return created;
  }

  async updateStaffAvailability(id: string, availability: Partial<InsertStaffAvailability>): Promise<StaffAvailability> {
    const [updated] = await db
      .update(staffAvailability)
      .set(availability)
      .where(eq(staffAvailability.id, id))
      .returning();
    return updated;
  }

  async deleteStaffAvailability(id: string): Promise<void> {
    await db.delete(staffAvailability).where(eq(staffAvailability.id, id));
  }

  // Staff Services operations
  async getStaffServices(staffId: string): Promise<(StaffServices & { service: Service })[]> {
    const results = await db
      .select()
      .from(staffServices)
      .leftJoin(services, eq(staffServices.serviceId, services.id))
      .where(eq(staffServices.staffId, staffId));

    return results.map(row => ({
      ...row.staff_services,
      service: row.services!,
    }));
  }

  async createStaffService(staffService: InsertStaffServices): Promise<StaffServices> {
    const [created] = await db.insert(staffServices).values(staffService).returning();
    return created;
  }

  async deleteStaffService(id: string): Promise<void> {
    await db.delete(staffServices).where(eq(staffServices.id, id));
  }

  async assignServiceToStaff(staffId: string, serviceId: string): Promise<StaffServices> {
    const [created] = await db
      .insert(staffServices)
      .values({ staffId, serviceId })
      .returning();
    return created;
  }

  async removeServiceFromStaff(staffId: string, serviceId: string): Promise<void> {
    await db
      .delete(staffServices)
      .where(and(
        eq(staffServices.staffId, staffId),
        eq(staffServices.serviceId, serviceId)
      ));
  }

  // Gallery operations
  async getGalleryImages(): Promise<GalleryImage[]> {
    return await db.select().from(galleryImages).orderBy(galleryImages.order, galleryImages.createdAt);
  }

  async getGalleryImagesByCategory(category: string): Promise<GalleryImage[]> {
    return await db
      .select()
      .from(galleryImages)
      .where(eq(galleryImages.category, category as any))
      .orderBy(galleryImages.order, galleryImages.createdAt);
  }

  async createGalleryImage(image: InsertGalleryImage): Promise<GalleryImage> {
    const [created] = await db.insert(galleryImages).values(image).returning();
    return created;
  }

  async updateGalleryImage(id: string, image: Partial<InsertGalleryImage>): Promise<GalleryImage> {
    const [updated] = await db
      .update(galleryImages)
      .set(image)
      .where(eq(galleryImages.id, id))
      .returning();
    return updated;
  }

  async deleteGalleryImage(id: string): Promise<void> {
    await db.delete(galleryImages).where(eq(galleryImages.id, id));
  }

  // Feedback operations
  async getFeedback(): Promise<(Feedback & { user?: User })[]> {
    const results = await db
      .select()
      .from(feedback)
      .leftJoin(users, eq(feedback.userId, users.id))
      .orderBy(desc(feedback.createdAt));

    return results.map(row => ({
      ...row.feedback,
      user: row.users || undefined,
    }));
  }

  async getApprovedFeedback(): Promise<(Feedback & { user?: User })[]> {
    const results = await db
      .select()
      .from(feedback)
      .leftJoin(users, eq(feedback.userId, users.id))
      .where(eq(feedback.status, 'APPROVED'))
      .orderBy(desc(feedback.createdAt));

    return results.map(row => ({
      ...row.feedback,
      user: row.users || undefined,
    }));
  }

  async getPendingFeedback(): Promise<(Feedback & { user?: User })[]> {
    const results = await db
      .select()
      .from(feedback)
      .leftJoin(users, eq(feedback.userId, users.id))
      .where(eq(feedback.status, 'PENDING'))
      .orderBy(desc(feedback.createdAt));

    return results.map(row => ({
      ...row.feedback,
      user: row.users || undefined,
    }));
  }

  async createFeedback(feedbackData: InsertFeedback): Promise<Feedback> {
    const [created] = await db.insert(feedback).values(feedbackData).returning();
    return created;
  }

  async updateFeedback(id: string, feedbackData: Partial<InsertFeedback>): Promise<Feedback> {
    const [updated] = await db
      .update(feedback)
      .set(feedbackData)
      .where(eq(feedback.id, id))
      .returning();
    return updated;
  }

  async deleteFeedback(id: string): Promise<void> {
    await db.delete(feedback).where(eq(feedback.id, id));
  }

  // Contact operations
  async getContactMessages(): Promise<ContactMessage[]> {
    return await db.select().from(contactMessages).orderBy(desc(contactMessages.createdAt));
  }

  async createContactMessage(message: InsertContactMessage): Promise<ContactMessage> {
    const [created] = await db.insert(contactMessages).values(message).returning();
    return created;
  }

  async updateContactMessage(id: string, message: Partial<InsertContactMessage>): Promise<ContactMessage> {
    const [updated] = await db
      .update(contactMessages)
      .set(message)
      .where(eq(contactMessages.id, id))
      .returning();
    return updated;
  }

  async deleteContactMessage(id: string): Promise<void> {
    await db.delete(contactMessages).where(eq(contactMessages.id, id));
  }

  // Content Settings operations
  async getContentSettings(): Promise<ContentSettings | undefined> {
    const [settings] = await db.select().from(contentSettings).limit(1);
    return settings;
  }

  async updateContentSettings(settings: InsertContentSettings): Promise<ContentSettings> {
    // First try to update existing record
    const existing = await this.getContentSettings();

    if (existing) {
      const [updated] = await db
        .update(contentSettings)
        .set({ ...settings, updatedAt: new Date() })
        .where(eq(contentSettings.id, existing.id))
        .returning();
      return updated;
    } else {
      // Create new record if none exists
      const [created] = await db.insert(contentSettings).values(settings).returning();
      return created;
    }
  }
}

export const storage = new DatabaseStorage();

