                <Home className="w-4 h-4 mr-3" />
                Back to Site
              </Button>
            </Link>
            <Button 
              variant="ghost" 
              className="w-full justify-start text-muted-foreground"
              onClick={() => logoutMutation.mutate()}
              data-testid="logout-button"
            >
              <LogOut className="w-4 h-4 mr-3" />
              Logout
