import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Sparkles, Eye, Leaf } from "lucide-react";
import customEyelashImage from "@assets/Thiáº¿t káº¿ chÆ°a cÃ³ tÃªn (8)_1757850541617.png";

interface Service {
  id: string;
  name: string;
  description: string;
  basePrice: string;
  effectivePrice?: number;
  hasDiscount?: boolean;
  isNail: boolean;
  isEyelash: boolean;
  isFacial: boolean;
}

interface ServicesSectionProps {
  services?: Service[];
}

export default function ServicesSection({ services = [] }: ServicesSectionProps) {
  // Group services by category based on boolean flags
  const servicesByCategory = services.reduce((acc, service) => {
    // Add service to multiple categories if it belongs to multiple
    if (service.isNail) {
      if (!acc["Nail"]) acc["Nail"] = [];
      acc["Nail"].push(service);
    }
    if (service.isEyelash) {
      if (!acc["Eyelash"]) acc["Eyelash"] = [];
      acc["Eyelash"].push(service);
    }
    if (service.isFacial) {
      if (!acc["Facial"]) acc["Facial"] = [];
      acc["Facial"].push(service);
    }
    return acc;
  }, {} as Record<string, Service[]>);

  const getCategoryIcon = (categoryName: string) => {
    switch (categoryName.toLowerCase()) {
      case 'nail':
        return <Sparkles className="text-white text-2xl" />;
      case 'eyelash':
        return <Eye className="text-white text-2xl" />;
      case 'facial':
        return <Leaf className="text-white text-2xl" />;
      default:
        return <Sparkles className="text-white text-2xl" />;
    }
  };

  const getCategoryImage = (categoryName: string) => {
    switch (categoryName.toLowerCase()) {
      case 'nail':
        return "https://images.unsplash.com/photo-1604654894610-df63bc536371?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&h=400";
      case 'eyelash':
        return customEyelashImage;
      case 'facial':
        return "https://images.unsplash.com/photo-1570172619644-dfd03ed5d881?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&h=400";
      default:
        return "https://images.unsplash.com/photo-1560750588-73207b1ef5b8?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&h=400";
    }
  };

  const getCategoryDescription = (categoryName: string) => {
    switch (categoryName.toLowerCase()) {
      case 'nail':
        return "Premium manicures, pedicures, nail art, and gel treatments using top-quality products for lasting beauty.";
      case 'eyelash':
        return "Professional eyelash extensions, lifts, and tinting to enhance your natural beauty and create stunning looks.";
      case 'facial':
        return "Rejuvenating facial treatments tailored to your skin type, using premium products for radiant, healthy skin.";
      default:
        return "Professional beauty services tailored to your needs.";
    }
  };

  return (
    <section id="services" className="py-20 bg-muted/30" data-testid="services-section">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <h2 className="text-4xl lg:text-5xl font-serif font-bold text-foreground mb-4" data-testid="services-title">
            Our Signature Services
          </h2>
          <p className="text-xl text-muted-foreground max-w-3xl mx-auto" data-testid="services-description">
            Indulge in our comprehensive beauty treatments designed to enhance your natural radiance
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {Object.entries(servicesByCategory).map(([categoryName, categoryServices]) => (
            <div 
              key={categoryName} 
              className="bg-card rounded-3xl p-8 shadow-lg hover-lift group"
              data-testid={`service-category-${categoryName.toLowerCase()}`}
            >
              <div className="mb-6">
                <img 
                  src={getCategoryImage(categoryName)} 
                  alt={`Professional ${categoryName.toLowerCase()} services`} 
                  className="w-full h-48 object-cover rounded-2xl mb-4"
                  data-testid={`img-${categoryName.toLowerCase()}`}
                />
                <div className="w-16 h-16 luxury-gradient rounded-2xl flex items-center justify-center mb-4">
                  {getCategoryIcon(categoryName)}
                </div>
              </div>

              <h3 className="text-2xl font-serif font-bold text-foreground mb-3" data-testid={`title-${categoryName.toLowerCase()}`}>
                {categoryName} Services
              </h3>

              <p className="text-muted-foreground mb-6" data-testid={`description-${categoryName.toLowerCase()}`}>
                {getCategoryDescription(categoryName)}
              </p>

              <div className="space-y-2 mb-6">
                {categoryServices.slice(0, 3).map((service) => (
                  <div key={service.id} className="flex justify-between items-center" data-testid={`service-${service.id}`}>
                    <span className="text-sm" data-testid={`service-name-${service.id}`}>{service.name}</span>
                    <div className="flex items-center space-x-2">
                      {service.hasDiscount && service.effectivePrice ? (
                        <>
                          <span className="text-xs text-muted-foreground line-through" data-testid={`original-price-${service.id}`}>
                            ${parseFloat(service.basePrice).toFixed(2)}
                          </span>
                          <span className="text-primary font-semibold" data-testid={`effective-price-${service.id}`}>
                            ${service.effectivePrice.toFixed(2)}
                          </span>
                          <Badge variant="secondary" className="text-xs" data-testid={`discount-badge-${service.id}`}>
                            SALE
                          </Badge>
                        </>
                      ) : (
                        <span className="text-primary font-semibold" data-testid={`price-${service.id}`}>
                          ${parseFloat(service.basePrice).toFixed(2)}
                        </span>
                      )}
                    </div>
                  </div>
                ))}
              </div>

              <Link href="/booking">
                <Button 
                  className="w-full bg-primary/10 text-primary hover:bg-primary hover:text-primary-foreground transition-all group-hover:bg-primary group-hover:text-primary-foreground"
                  data-testid={`button-book-${categoryName.toLowerCase()}`}
                >
                  Book {categoryName} Service
                </Button>
              </Link>
            </div>
          ))}
        </div>

        {Object.keys(servicesByCategory).length === 0 && (
          <div className="text-center py-12">
            <p className="text-muted-foreground" data-testid="text-no-services">
              No services available at the moment. Please check back later.
            </p>
          </div>
        )}
      </div>
    </section>
  );
}

client/src/components/TestimonialsSectio.content:
import { Star } from "lucide-react";

interface Feedback {
  id: string;
  rating: number;
  title?: string;
  comment: string;
  customerName?: string;
  user?: {
    firstName?: string;
    lastName?: string;
  };
}

interface TestimonialsSectionProps {
  feedback?: Feedback[];
}

export default function TestimonialsSection({ feedback = [] }: TestimonialsSectionProps) {
  // Fallback testimonials if no feedback is provided
  const fallbackTestimonials = [
    {
      id: '1',
      rating: 5,
      comment: "Absolutely amazing service! The nail art was exactly what I wanted and the staff was so professional. Will definitely be coming back!",
      customerName: "Sarah Johnson",
      role: "Regular Client"
    },
    {
      id: '2',
      rating: 5, 
      comment: "The facial treatment was incredible! My skin has never looked better. The atmosphere is so relaxing and luxurious.",
      customerName: "Emma Davis",
      role: "Facial Client"
    },
    {
      id: '3',
      rating: 5,
      comment: "Best eyelash extensions I've ever had! They look so natural yet dramatic. The technician was skilled and gentle.",
      customerName: "Maya Rodriguez", 
      role: "Lash Client"
    }
  ];

  const displayTestimonials = feedback.length > 0 
    ? feedback.filter(f => f.rating >= 4).slice(0, 3)
    : fallbackTestimonials;

  const getCustomerName = (testimonial: any) => {
    if (testimonial.customerName) return testimonial.customerName;
    if (testimonial.user?.firstName) {
      return `${testimonial.user.firstName} ${testimonial.user.lastName || ''}`.trim();
    }
    return "Anonymous Customer";
  };

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star 
        key={i} 
        className={`w-4 h-4 ${i < rating ? 'text-accent fill-current' : 'text-muted-foreground'}`}
      />
    ));
  };

  return (
    <section className="py-20 bg-muted/30" data-testid="testimonials-section">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-12">
          <h2 className="text-4xl font-serif font-bold text-foreground mb-4" data-testid="testimonials-title">
            What Our Clients Say
          </h2>
          <p className="text-xl text-muted-foreground" data-testid="testimonials-description">
            Real feedback from our satisfied customers
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {displayTestimonials.map((testimonial) => (
            <div 
              key={testimonial.id} 
              className="bg-card rounded-3xl p-8 shadow-lg"
              data-testid={`testimonial-${testimonial.id}`}
            >
              <div className="flex items-center mb-4" data-testid={`rating-${testimonial.id}`}>
                <div className="flex">
                  {renderStars(testimonial.rating)}
                </div>
              </div>

              <p className="text-muted-foreground mb-6" data-testid={`comment-${testimonial.id}`}>
                "{testimonial.comment}"
              </p>

              <div className="flex items-center">
                <img 
                  src={`https://api.dicebear.com/7.x/avataaars/svg?seed=${getCustomerName(testimonial)}`}
                  alt={`${getCustomerName(testimonial)} avatar`} 
                  className="w-12 h-12 rounded-full object-cover mr-4"
                  data-testid={`avatar-${testimonial.id}`}
                />
                <div>
                  <h4 className="font-semibold text-foreground" data-testid={`name-${testimonial.id}`}>
                    {getCustomerName(testimonial)}
                  </h4>
                  <p className="text-sm text-muted-foreground" data-testid={`role-${testimonial.id}`}>
                    {(testimonial as any).role || "Valued Client"}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>

        {displayTestimonials.length === 0 && (
          <div className="text-center py-12">
            <p className="text-muted-foreground" data-testid="text-no-testimonials">
              No customer testimonials available yet.
            </p>
          </div>
        )}
      </div>
    </section>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

