                </CardHeader>
                <CardContent>
                  <form onSubmit={handleSubmit} className="space-y-6" data-testid="contact-form">
                    <div>
                      <Label htmlFor="name" className="text-sm font-semibold text-foreground mb-2 block">
                        Full Name *
                      </Label>
                      <Input
                        id="name"
                        type="text"
                        placeholder="Your full name"
                        value={formData.name}
                        onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                        data-testid="input-name"
                      />
                    </div>

                    <div>
                      <Label htmlFor="phone" className="text-sm font-semibold text-foreground mb-2 block">
                        Phone Number
                      </Label>
                      <Input
                        id="phone"
                        type="tel"
                        placeholder="Your phone number"
                        value={formData.phone}
                        onChange={(e) => setFormData(prev => ({ ...prev, phone: e.target.value }))}
                        data-testid="input-phone"
                      />
                    </div>

                    <div>
                      <Label htmlFor="message" className="text-sm font-semibold text-foreground mb-2 block">
                        Message *
                      </Label>
                      <Textarea
                        id="message"
                        rows={4}
                        placeholder="How can we help you?"
                        value={formData.message}
                        onChange={(e) => setFormData(prev => ({ ...prev, message: e.target.value }))}
                        data-testid="textarea-message"
                      />
                    </div>

                    <Button
                      type="submit"
                      disabled={createMessageMutation.isPending}
                      className="w-full bg-primary text-primary-foreground hover:bg-primary/90"
                      data-testid="button-send-message"
                    >
                      <Send className="w-4 h-4 mr-2" />
                      {createMessageMutation.isPending ? "Sending..." : "Send Message"}
                    </Button>
                  </form>
                </CardContent>
              </Card>
            </div>
          </div>
        </div>
      </section>

      <Footer contentSettings={contentSettings} />
    </div>
  );
}

client/src/pages/feedback.tsx.content:
import { useEffect, useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import Navigation from "@/components/Navigation";
import Footer from "@/components/Footer";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { apiRequest } from "@/lib/queryClient";
import { insertFeedbackSchema } from "@shared/schema";
import { Star, Send } from "lucide-react";

export default function Feedback() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [rating, setRating] = useState(0);
  const [hoverRating, setHoverRating] = useState(0);
  const [formData, setFormData] = useState({
    title: "",
    comment: "",
    customerName: ""
  });

  useEffect(() => {
    document.title = "Leave Feedback - HOME BASE Beauty Salon";

    // Pre-fill customer name if logged in
    if (user) {
      setFormData(prev => ({
        ...prev,
        customerName: `${user.firstName || ''} ${user.lastName || ''}`.trim()
      }));
    }
  }, [user]);

  const { data: approvedFeedback = [] } = useQuery({
    queryKey: ["/api/feedback"],
  });

  const { data: contentSettings } = useQuery({
    queryKey: ["/api/content-settings"],
  });

  const createFeedbackMutation = useMutation({
    mutationFn: async (feedbackData: any) => {
      return apiRequest("POST", "/api/feedback", feedbackData);
    },
    onSuccess: () => {
      toast({
        title: "Feedback Submitted!",
        description: "Thank you for your feedback. It will be reviewed before appearing on our site.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/feedback"] });

      // Reset form
      setRating(0);
      setFormData({
        title: "",
        comment: "",
        customerName: user ? `${user.firstName || ''} ${user.lastName || ''}`.trim() : ""
      });
    },
    onError: (error) => {
      toast({
        title: "Submission Failed",
        description: error.message || "Failed to submit feedback. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (rating === 0 || !formData.comment.trim() || !formData.customerName.trim()) {
      toast({
        title: "Missing Information",
        description: "Please provide a rating, comment, and your name.",
        variant: "destructive",
      });
      return;
    }

    const feedbackData = {
      userId: user?.id || null,
      rating,
      title: formData.title.trim() || null,
      comment: formData.comment.trim(),
      customerName: formData.customerName.trim(),
      imageUrls: [], // TODO: Add image upload functionality
    };

    createFeedbackMutation.mutate(feedbackData);
  };

  const renderStars = (count: number, interactive = false) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star
        key={i}
        className={`w-6 h-6 cursor-pointer transition-colors ${
          i < (interactive ? (hoverRating || rating) : count)
            ? 'text-accent fill-current' 
            : 'text-muted-foreground'
        }`}
        onClick={interactive ? () => setRating(i + 1) : undefined}
        onMouseEnter={interactive ? () => setHoverRating(i + 1) : undefined}
        onMouseLeave={interactive ? () => setHoverRating(0) : undefined}
        data-testid={interactive ? `star-input-${i + 1}` : `star-display-${i + 1}`}
      />
    ));
  };

  const getCustomerName = (feedback: any) => {
    if (feedback.customerName) return feedback.customerName;
    if (feedback.user?.firstName) {
      return `${feedback.user.firstName} ${feedback.user.lastName || ''}`.trim();
    }
