import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import AdminLayout from "@/components/AdminLayout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Plus, Edit, Trash2, DollarSign, Clock } from "lucide-react";

export default function AdminServices() {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [serviceDialog, setServiceDialog] = useState(false);
  const [editingService, setEditingService] = useState<any>(null);
  const [serviceForm, setServiceForm] = useState({
    name: "",
    slug: "",
    description: "",
    basePrice: "",
    durationMins: "",
    isNail: false,
    isEyelash: false,
    isFacial: false,
    isActive: true
  });

  useEffect(() => {
    document.title = "Services Management - Admin";

    if (!isLoading && (!isAuthenticated || user?.role !== 'ADMIN')) {
      toast({
        title: "Unauthorized",
        description: "Admin access required.",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, user, toast]);

  const { data: services = [], error: servicesError } = useQuery<any[]>({
    queryKey: ["/api/services"],
    enabled: isAuthenticated && user?.role === 'ADMIN',
    retry: false,
  });

  // Handle unauthorized errors
  useEffect(() => {
    const errors = [servicesError].filter(Boolean);
    for (const error of errors) {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "Admin session expired. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
    }
  }, [servicesError, toast]);

  const createServiceMutation = useMutation({
    mutationFn: async (serviceData: any) => {
      const formattedData = {
        ...serviceData,
        basePrice: serviceData.basePrice.toString(),
        durationMins: parseInt(serviceData.durationMins)
      };
      return apiRequest("POST", "/api/admin/services", formattedData);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Service created successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
      setServiceDialog(false);
      resetForm();
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "Admin session expired. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error.message || "Failed to create service.",
        variant: "destructive",
      });
    },
  });

  const updateServiceMutation = useMutation({
    mutationFn: async ({ id, ...serviceData }: any) => {
      const formattedData = {
        ...serviceData,
        basePrice: serviceData.basePrice.toString(),
        durationMins: parseInt(serviceData.durationMins)
      };
      return apiRequest("PUT", `/api/admin/services/${id}`, formattedData);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Service updated successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
      setServiceDialog(false);
      resetForm();
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "Admin session expired. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error.message || "Failed to update service.",
        variant: "destructive",
      });
    },
  });

  const deleteServiceMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/admin/services/${id}`);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Service deleted successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/services"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "Admin session expired. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error.message || "Failed to delete service.",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setServiceForm({
      name: "",
      slug: "",
      description: "",
      basePrice: "",
      durationMins: "",
      isNail: false,
      isEyelash: false,
      isFacial: false,
      isActive: true
    });
    setEditingService(null);
  };

  const openEditDialog = (service: any) => {
    setEditingService(service);
    setServiceForm({
      name: service.name,
      slug: service.slug,
      description: service.description || "",
      basePrice: service.basePrice,
      durationMins: service.durationMins.toString(),
      isNail: service.isNail || false,
      isEyelash: service.isEyelash || false,
      isFacial: service.isFacial || false,
      isActive: service.isActive
    });
    setServiceDialog(true);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!serviceForm.name || !serviceForm.basePrice || !serviceForm.durationMins || (!serviceForm.isNail && !serviceForm.isEyelash && !serviceForm.isFacial)) {
      toast({
        title: "Missing Information",
        description: "Please fill in all required fields and select at least one category.",
        variant: "destructive",
      });
      return;
    }

    const serviceData = {
      ...serviceForm,
      basePrice: parseFloat(serviceForm.basePrice),
      durationMins: parseInt(serviceForm.durationMins),
      slug: serviceForm.slug || serviceForm.name.toLowerCase().replace(/\s+/g, '-')
    };

    if (editingService) {
      updateServiceMutation.mutate({ id: editingService.id, ...serviceData });
    } else {
      createServiceMutation.mutate(serviceData);
    }
  };

  if (isLoading || (!isAuthenticated || user?.role !== 'ADMIN')) {
    return (
      <AdminLayout>
        <div className="animate-pulse">
          <div className="h-8 bg-muted rounded w-1/3 mb-6"></div>
          <div className="space-y-4">
            {Array.from({ length: 3 }, (_, i) => (
              <div key={i} className="h-32 bg-muted rounded-lg"></div>
            ))}
          </div>
        </div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className="space-y-8">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-serif font-bold text-foreground" data-testid="services-title">
              Services Management
            </h1>
            <p className="text-muted-foreground" data-testid="services-description">
              Manage your salon services and pricing
            </p>
          </div>
          <Dialog open={serviceDialog} onOpenChange={setServiceDialog}>
            <DialogTrigger asChild>
              <Button onClick={resetForm} data-testid="button-add-service">
                <Plus className="w-4 h-4 mr-2" />
                Add Service
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl" data-testid="service-dialog">
              <DialogHeader>
                <DialogTitle data-testid="dialog-title">
                  {editingService ? "Edit Service" : "Add New Service"}
                </DialogTitle>
              </DialogHeader>
              <form onSubmit={handleSubmit} className="space-y-6" data-testid="service-form">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label className="text-sm font-semibold mb-2 block">
                      Categories *
                    </Label>
                    <div className="space-y-2">
                      <div className="flex items-center space-x-2">
                        <input
                          type="checkbox"
                          id="isNail"
                          checked={serviceForm.isNail}
                          onChange={(e) => setServiceForm(prev => ({ ...prev, isNail: e.target.checked }))}
                          className="rounded border-border"
                          data-testid="checkbox-nail"
                        />
                        <Label htmlFor="isNail" className="text-sm">
                          Nail
                        </Label>
                      </div>
                      <div className="flex items-center space-x-2">
                        <input
                          type="checkbox"
                          id="isEyelash"
                          checked={serviceForm.isEyelash}
                          onChange={(e) => setServiceForm(prev => ({ ...prev, isEyelash: e.target.checked }))}
                          className="rounded border-border"
                          data-testid="checkbox-eyelash"
                        />
                        <Label htmlFor="isEyelash" className="text-sm">
                          Eyelash
                        </Label>
                      </div>
                      <div className="flex items-center space-x-2">
                        <input
                          type="checkbox"
                          id="isFacial"
                          checked={serviceForm.isFacial}
                          onChange={(e) => setServiceForm(prev => ({ ...prev, isFacial: e.target.checked }))}
                          className="rounded border-border"
                          data-testid="checkbox-facial"
                        />
                        <Label htmlFor="isFacial" className="text-sm">
                          Facial
                        </Label>
                      </div>
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="name" className="text-sm font-semibold mb-2 block">
                      Service Name *
                    </Label>
                    <Input
                      id="name"
                      value={serviceForm.name}
                      onChange={(e) => setServiceForm(prev => ({ ...prev, name: e.target.value }))}
                      placeholder="Enter service name"
                      data-testid="input-name"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="description" className="text-sm font-semibold mb-2 block">
                    Description
                  </Label>
                  <Textarea
                    id="description"
                    value={serviceForm.description}
                    onChange={(e) => setServiceForm(prev => ({ ...prev, description: e.target.value }))}
                    placeholder="Service description"
                    rows={3}
                    data-testid="textarea-description"
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="basePrice" className="text-sm font-semibold mb-2 block">
                      Base Price ($) *
                    </Label>
                    <Input
                      id="basePrice"
                      type="number"
                      step="0.01"
                      value={serviceForm.basePrice}
                      onChange={(e) => setServiceForm(prev => ({ ...prev, basePrice: e.target.value }))}
                      placeholder="0.00"
                      data-testid="input-base-price"
                    />
                  </div>

                  <div>
                    <Label htmlFor="durationMins" className="text-sm font-semibold mb-2 block">
                      Duration (minutes) *
                    </Label>
                    <Input
                      id="durationMins"
                      type="number"
                      value={serviceForm.durationMins}
                      onChange={(e) => setServiceForm(prev => ({ ...prev, durationMins: e.target.value }))}
                      placeholder="60"
                      data-testid="input-duration"
                    />
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    id="isActive"
                    checked={serviceForm.isActive}
                    onChange={(e) => setServiceForm(prev => ({ ...prev, isActive: e.target.checked }))}
                    className="rounded border-border"
                    data-testid="checkbox-active"
                  />
                  <Label htmlFor="isActive" className="text-sm">
                    Service is active
                  </Label>
                </div>

                <div className="flex justify-end space-x-4">
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setServiceDialog(false)}
                    data-testid="button-cancel"
                  >
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={createServiceMutation.isPending || updateServiceMutation.isPending}
                    data-testid="button-save"
                  >
                    {editingService ? "Update" : "Create"}
                  </Button>
                </div>
              </form>
            </DialogContent>
          </Dialog>
        </div>

        {/* Services Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6" data-testid="services-grid">
          {services.map((service: any) => (
            <Card key={service.id} className="hover-lift" data-testid={`service-card-${service.id}`}>
              <CardHeader>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <CardTitle className="text-lg" data-testid={`service-name-${service.id}`}>
                      {service.name}
                    </CardTitle>
                    <p className="text-sm text-muted-foreground" data-testid={`service-category-${service.id}`}>
                      {[
                        service.isNail && "Nail",
                        service.isEyelash && "Eyelash", 
                        service.isFacial && "Facial"
                      ].filter(Boolean).join(", ") || "No categories"}
                    </p>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Badge variant={service.isActive ? "default" : "secondary"} data-testid={`service-status-${service.id}`}>
                      {service.isActive ? "Active" : "Inactive"}
                    </Badge>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {service.description && (
                    <p className="text-sm text-muted-foreground line-clamp-2" data-testid={`service-description-${service.id}`}>
                      {service.description}
                    </p>
                  )}

                  <div className="flex items-center justify-between text-sm">
                    <div className="flex items-center space-x-1" data-testid={`service-price-${service.id}`}>
                      <DollarSign className="w-4 h-4 text-primary" />
                      <span className="font-semibold">${parseFloat(service.basePrice).toFixed(2)}</span>
                    </div>
                    <div className="flex items-center space-x-1" data-testid={`service-duration-${service.id}`}>
                      <Clock className="w-4 h-4 text-muted-foreground" />
                      <span>{service.durationMins} mins</span>
                    </div>
                  </div>

                  {service.hasDiscount && (
                    <Badge variant="secondary" className="w-full justify-center" data-testid={`service-discount-${service.id}`}>
                      On Sale: ${service.effectivePrice?.toFixed(2)}
                    </Badge>
                  )}
                </div>

                <div className="flex items-center space-x-2 mt-4">
                  <Button 
                    variant="outline" 
                    size="sm" 
                    className="flex-1"
                    onClick={() => openEditDialog(service)}
                    data-testid={`button-edit-${service.id}`}
                  >
                    <Edit className="w-4 h-4 mr-1" />
                    Edit
                  </Button>
                  <AlertDialog>
                    <AlertDialogTrigger asChild>
                      <Button variant="destructive" size="sm" data-testid={`button-delete-${service.id}`}>
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </AlertDialogTrigger>
                    <AlertDialogContent data-testid={`delete-dialog-${service.id}`}>
                      <AlertDialogHeader>
                        <AlertDialogTitle>Delete Service</AlertDialogTitle>
                        <AlertDialogDescription>
                          Are you sure you want to delete "{service.name}"? This action cannot be undone.
                        </AlertDialogDescription>
                      </AlertDialogHeader>
                      <AlertDialogFooter>
                        <AlertDialogCancel data-testid={`cancel-delete-${service.id}`}>Cancel</AlertDialogCancel>
                        <AlertDialogAction
                          onClick={() => deleteServiceMutation.mutate(service.id)}
                          className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                          data-testid={`confirm-delete-${service.id}`}
                        >
                          Delete
                        </AlertDialogAction>
                      </AlertDialogFooter>
                    </AlertDialogContent>
                  </AlertDialog>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {services.length === 0 && (
          <Card className="text-center py-12" data-testid="no-services-card">
            <CardContent>
              <p className="text-muted-foreground mb-4" data-testid="no-services-text">
                No services found. Create your first service to get started.
              </p>
              <Button onClick={() => setServiceDialog(true)} data-testid="button-create-first-service">
                <Plus className="w-4 h-4 mr-2" />
                Create First Service
              </Button>
            </CardContent>
          </Card>
        )}
      </div>
    </AdminLayout>
  );
}

client/src/pages/admin/settings.tsx.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import AdminLayout from "@/components/AdminLayout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { 
  Settings as SettingsIcon, 
  Save, 
  MapPin, 
  Phone, 
  Facebook, 
  Instagram, 
  MessageCircle, 
  Plus, 
  X,
  Globe,
  Clock
} from "lucide-react";

export default function AdminSettings() {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [settingsForm, setSettingsForm] = useState({
    slogans: [""],
    address: "",
    facebookUrl: "",
    zaloUrl: "",
    instagramUrl: "",
    phone: "",
    openingHours: {
      monday: { open: "09:00", close: "19:00", closed: false },
      tuesday: { open: "09:00", close: "19:00", closed: false },
      wednesday: { open: "09:00", close: "19:00", closed: false },
      thursday: { open: "09:00", close: "19:00", closed: false },
      friday: { open: "09:00", close: "19:00", closed: false },
      saturday: { open: "09:00", close: "18:00", closed: false },
      sunday: { open: "10:00", close: "17:00", closed: false }
    },
    heroImages: [""],
    seo: {
      title: "",
      description: "",
      keywords: ""
    }
  });

  useEffect(() => {
    document.title = "Settings - Admin";

    if (!isLoading && (!isAuthenticated || user?.role !== 'ADMIN')) {
      toast({
        title: "Unauthorized",
        description: "Admin access required.",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, user, toast]);

  const { data: contentSettings, error: settingsError } = useQuery({
    queryKey: ["/api/content-settings"],
    enabled: isAuthenticated && user?.role === 'ADMIN',
    retry: false,
  });

  // Load existing settings when data is available
  useEffect(() => {
    if (contentSettings) {
      setSettingsForm({
        slogans: contentSettings.slogans?.length > 0 ? contentSettings.slogans : [""],
        address: contentSettings.address || "",
        facebookUrl: contentSettings.facebookUrl || "",
        zaloUrl: contentSettings.zaloUrl || "",
        instagramUrl: contentSettings.instagramUrl || "",
        phone: contentSettings.phone || "",
        openingHours: contentSettings.openingHours || {
          monday: { open: "09:00", close: "19:00", closed: false },
          tuesday: { open: "09:00", close: "19:00", closed: false },
          wednesday: { open: "09:00", close: "19:00", closed: false },
          thursday: { open: "09:00", close: "19:00", closed: false },
          friday: { open: "09:00", close: "19:00", closed: false },
          saturday: { open: "09:00", close: "18:00", closed: false },
          sunday: { open: "10:00", close: "17:00", closed: false }
        },
        heroImages: contentSettings.heroImages?.length > 0 ? contentSettings.heroImages : [""],
        seo: contentSettings.seo || {
          title: "",
          description: "",
          keywords: ""
        }
      });
    }
  }, [contentSettings]);

  // Handle unauthorized errors
  useEffect(() => {
    if (settingsError && isUnauthorizedError(settingsError as Error)) {
      toast({
        title: "Unauthorized",
        description: "Admin session expired. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [settingsError, toast]);

  const updateSettingsMutation = useMutation({
    mutationFn: async (settingsData: any) => {
      return apiRequest("PUT", "/api/admin/content-settings", settingsData);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Settings updated successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/content-settings"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "Admin session expired. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error.message || "Failed to update settings.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const settingsData = {
      ...settingsForm,
      slogans: settingsForm.slogans.filter(slogan => slogan.trim() !== ""),
      heroImages: settingsForm.heroImages.filter(url => url.trim() !== "")
    };

    updateSettingsMutation.mutate(settingsData);
  };

  const addSlogan = () => {
    setSettingsForm(prev => ({
      ...prev,
      slogans: [...prev.slogans, ""]
    }));
  };

  const removeSlogan = (index: number) => {
    setSettingsForm(prev => ({
      ...prev,
      slogans: prev.slogans.filter((_, i) => i !== index)
    }));
  };

  const updateSlogan = (index: number, value: string) => {
    setSettingsForm(prev => ({
      ...prev,
      slogans: prev.slogans.map((slogan, i) => i === index ? value : slogan)
    }));
  };

  const addHeroImage = () => {
    setSettingsForm(prev => ({
      ...prev,
      heroImages: [...prev.heroImages, ""]
    }));
  };

  const removeHeroImage = (index: number) => {
    setSettingsForm(prev => ({
      ...prev,
      heroImages: prev.heroImages.filter((_, i) => i !== index)
    }));
  };

  const updateHeroImage = (index: number, value: string) => {
    setSettingsForm(prev => ({
      ...prev,
      heroImages: prev.heroImages.map((url, i) => i === index ? value : url)
    }));
  };

  const updateOpeningHours = (day: string, field: string, value: string | boolean) => {
    setSettingsForm(prev => ({
      ...prev,
      openingHours: {
        ...prev.openingHours,
        [day]: {
          ...prev.openingHours[day as keyof typeof prev.openingHours],
          [field]: value
        }
      }
    }));
  };

  const updateSeo = (field: string, value: string) => {
    setSettingsForm(prev => ({
      ...prev,
      seo: {
        ...prev.seo,
        [field]: value
      }
    }));
  };

  const daysOfWeek = [
    { key: 'monday', label: 'Monday' },
    { key: 'tuesday', label: 'Tuesday' },
    { key: 'wednesday', label: 'Wednesday' },
    { key: 'thursday', label: 'Thursday' },
    { key: 'friday', label: 'Friday' },
    { key: 'saturday', label: 'Saturday' },
    { key: 'sunday', label: 'Sunday' }
  ];

  if (isLoading || (!isAuthenticated || user?.role !== 'ADMIN')) {
    return (
      <AdminLayout>
        <div className="animate-pulse">
          <div className="h-8 bg-muted rounded w-1/3 mb-6"></div>
          <div className="space-y-6">
            {Array.from({ length: 4 }, (_, i) => (
              <div key={i} className="h-48 bg-muted rounded-lg"></div>
            ))}
          </div>
        </div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className="space-y-8">
        {/* Header */}
        <div>
          <h1 className="text-3xl font-serif font-bold text-foreground" data-testid="settings-title">
            Settings
          </h1>
          <p className="text-muted-foreground" data-testid="settings-description">
            Manage your salon's content and configuration settings
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-8" data-testid="settings-form">
          {/* Brand & Slogans */}
          <Card data-testid="brand-settings-card">
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <SettingsIcon className="w-5 h-5 text-primary" />
                <span>Brand & Slogans</span>
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label className="text-sm font-semibold mb-3 block">
                  Salon Slogans
                </Label>
                <div className="space-y-3">
                  {settingsForm.slogans.map((slogan, index) => (
                    <div key={index} className="flex items-center space-x-2">
                      <Input
                        value={slogan}
                        onChange={(e) => updateSlogan(index, e.target.value)}
                        placeholder="Enter a slogan"
                        data-testid={`input-slogan-${index}`}
                      />
                      {settingsForm.slogans.length > 1 && (
                        <Button
                          type="button"
                          variant="outline"
                          size="icon"
                          onClick={() => removeSlogan(index)}
                          data-testid={`button-remove-slogan-${index}`}
                        >
                          <X className="w-4 h-4" />
                        </Button>
                      )}
                    </div>
                  ))}
                  <Button
                    type="button"
                    variant="outline"
                    size="sm"
                    onClick={addSlogan}
                    className="w-full"
                    data-testid="button-add-slogan"
                  >
                    <Plus className="w-4 h-4 mr-2" />
                    Add Slogan
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Contact Information */}
          <Card data-testid="contact-settings-card">
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <Phone className="w-5 h-5 text-primary" />
                <span>Contact Information</span>
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="phone" className="text-sm font-semibold mb-2 block">
                    Phone Number
                  </Label>
                  <Input
                    id="phone"
                    value={settingsForm.phone}
                    onChange={(e) => setSettingsForm(prev => ({ ...prev, phone: e.target.value }))}
                    placeholder="+65 9712 1097"
                    data-testid="input-phone"
                  />
                </div>

                <div>
                  <Label htmlFor="address" className="text-sm font-semibold mb-2 block">
                    Address
                  </Label>
                  <Textarea
                    id="address"
                    value={settingsForm.address}
                    onChange={(e) => setSettingsForm(prev => ({ ...prev, address: e.target.value }))}
                    placeholder="Blk 483A Yishun Ave 6 Singapore 761483"
                    rows={2}
                    data-testid="textarea-address"
                  />
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Social Media */}
          <Card data-testid="social-settings-card">
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <Globe className="w-5 h-5 text-primary" />
                <span>Social Media Links</span>
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <Label htmlFor="facebookUrl" className="text-sm font-semibold mb-2 block flex items-center space-x-2">
                    <Facebook className="w-4 h-4" />
                    <span>Facebook</span>
                  </Label>
                  <Input
                    id="facebookUrl"
                    value={settingsForm.facebookUrl}
                    onChange={(e) => setSettingsForm(prev => ({ ...prev, facebookUrl: e.target.value }))}
                    placeholder="https://facebook.com/homebasebeauty"
                    data-testid="input-facebook"
                  />
                </div>

                <div>
                  <Label htmlFor="instagramUrl" className="text-sm font-semibold mb-2 block flex items-center space-x-2">
                    <Instagram className="w-4 h-4" />
                    <span>Instagram</span>
                  </Label>
                  <Input
                    id="instagramUrl"
                    value={settingsForm.instagramUrl}
                    onChange={(e) => setSettingsForm(prev => ({ ...prev, instagramUrl: e.target.value }))}
                    placeholder="https://instagram.com/homebasebeauty"
                    data-testid="input-instagram"
                  />
                </div>

                <div>
                  <Label htmlFor="zaloUrl" className="text-sm font-semibold mb-2 block flex items-center space-x-2">
                    <MessageCircle className="w-4 h-4" />
                    <span>Zalo</span>
                  </Label>
                  <Input
                    id="zaloUrl"
                    value={settingsForm.zaloUrl}
                    onChange={(e) => setSettingsForm(prev => ({ ...prev, zaloUrl: e.target.value }))}
                    placeholder="https://zalo.me/homebasebeauty"
                    data-testid="input-zalo"
                  />
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Opening Hours */}
          <Card data-testid="hours-settings-card">
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <Clock className="w-5 h-5 text-primary" />
                <span>Opening Hours</span>
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              {daysOfWeek.map((day) => {
                const dayHours = settingsForm.openingHours[day.key as keyof typeof settingsForm.openingHours];
                return (
                  <div key={day.key} className="grid grid-cols-1 md:grid-cols-4 gap-4 items-center" data-testid={`hours-${day.key}`}>
                    <div className="flex items-center space-x-2">
                      <Label className="font-medium min-w-[80px]">{day.label}</Label>
                    </div>

                    {!dayHours.closed ? (
                      <>
                        <div>
                          <Label className="text-xs text-muted-foreground mb-1 block">Open</Label>
                          <Input
                            type="time"
                            value={dayHours.open}
                            onChange={(e) => updateOpeningHours(day.key, 'open', e.target.value)}
                            data-testid={`input-${day.key}-open`}
                          />
                        </div>
                        <div>
                          <Label className="text-xs text-muted-foreground mb-1 block">Close</Label>
                          <Input
                            type="time"
                            value={dayHours.close}
                            onChange={(e) => updateOpeningHours(day.key, 'close', e.target.value)}
                            data-testid={`input-${day.key}-close`}
                          />
                        </div>
                      </>
                    ) : (
                      <div className="col-span-2 flex items-center">
                        <Badge variant="secondary">Closed</Badge>
                      </div>
                    )}

                    <div className="flex items-center space-x-2">
                      <input
                        type="checkbox"
                        id={`${day.key}-closed`}
                        checked={dayHours.closed}
                        onChange={(e) => updateOpeningHours(day.key, 'closed', e.target.checked)}
                        className="rounded border-border"
                        data-testid={`checkbox-${day.key}-closed`}
                      />
                      <Label htmlFor={`${day.key}-closed`} className="text-sm">
                        Closed
                      </Label>
                    </div>
                  </div>
                );
              })}
            </CardContent>
          </Card>

          {/* Hero Images */}
          <Card data-testid="hero-settings-card">
            <CardHeader>
              <CardTitle>Hero Images</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label className="text-sm font-semibold mb-3 block">
                  Hero Section Images
                </Label>
                <div className="space-y-3">
                  {settingsForm.heroImages.map((url, index) => (
                    <div key={index} className="flex items-center space-x-2">
                      <Input
                        value={url}
                        onChange={(e) => updateHeroImage(index, e.target.value)}
                        placeholder="https://example.com/hero-image.jpg"
                        data-testid={`input-hero-image-${index}`}
                      />
                      {settingsForm.heroImages.length > 1 && (
                        <Button
                          type="button"
                          variant="outline"
                          size="icon"
                          onClick={() => removeHeroImage(index)}
                          data-testid={`button-remove-hero-image-${index}`}
                        >
                          <X className="w-4 h-4" />
                        </Button>
                      )}
                    </div>
                  ))}
                  <Button
                    type="button"
                    variant="outline"
                    size="sm"
                    onClick={addHeroImage}
                    className="w-full"
                    data-testid="button-add-hero-image"
                  >
                    <Plus className="w-4 h-4 mr-2" />
                    Add Hero Image
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* SEO Settings */}
          <Card data-testid="seo-settings-card">
            <CardHeader>
              <CardTitle>SEO Settings</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="seoTitle" className="text-sm font-semibold mb-2 block">
                  Site Title
                </Label>
                <Input
                  id="seoTitle"
                  value={settingsForm.seo.title}
                  onChange={(e) => updateSeo('title', e.target.value)}
                  placeholder="HOME BASE Beauty Salon - Luxury Nail, Eyelash & Facial Services"
                  data-testid="input-seo-title"
                />
              </div>

              <div>
                <Label htmlFor="seoDescription" className="text-sm font-semibold mb-2 block">
                  Meta Description
                </Label>
                <Textarea
                  id="seoDescription"
                  value={settingsForm.seo.description}
                  onChange={(e) => updateSeo('description', e.target.value)}
                  placeholder="Experience luxury beauty services with our expert team. Specializing in premium nail care, eyelash extensions, and rejuvenating facial treatments."
                  rows={3}
                  data-testid="textarea-seo-description"
                />
              </div>

              <div>
                <Label htmlFor="seoKeywords" className="text-sm font-semibold mb-2 block">
                  Keywords
                </Label>
                <Input
                  id="seoKeywords"
                  value={settingsForm.seo.keywords}
                  onChange={(e) => updateSeo('keywords', e.target.value)}
                  placeholder="beauty salon, nail care, eyelash extensions, facial treatments, luxury spa"
                  data-testid="input-seo-keywords"
                />
              </div>
            </CardContent>
          </Card>

          {/* Save Button */}
          <div className="flex justify-end">
            <Button 
              type="submit" 
              size="lg"
              disabled={updateSettingsMutation.isPending}
              data-testid="button-save-settings"
            >
              <Save className="w-4 h-4 mr-2" />
              {updateSettingsMutation.isPending ? "Saving..." : "Save Settings"}
            </Button>
          </div>
        </form>
      </div>
    </AdminLayout>
  );
}

client/src/components/CalendarView.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { 
  ChevronLeft, 
  ChevronRight, 
  Clock, 
  User, 
  Phone, 
  Mail, 
  Calendar as CalendarIcon,
  CheckCircle, 
  XCircle, 
  AlertCircle 
} from "lucide-react";
import { format, 
  startOfMonth, 
  endOfMonth, 
  startOfWeek, 
  endOfWeek, 
  eachDayOfInterval, 
  isSameDay, 
  isSameMonth, 
  addMonths, 
  subMonths 
} from "date-fns";
import type { Booking, Service, ServiceCategory, User as DBUser, Staff } from "@shared/schema";

// Extended booking type with populated relations for calendar display
export interface BookingWithRelations extends Omit<Booking, 'userId' | 'serviceId' | 'staffId'> {
  user?: DBUser;
  service: Service & {
    category: ServiceCategory;
  };
  staff?: Staff;
}

interface CalendarViewProps {
  bookings: BookingWithRelations[];
  onUpdateBookingStatus?: (bookingId: string, status: string) => void;
  onEditBooking?: (booking: BookingWithRelations) => void;
}

export default function CalendarView({ bookings, onUpdateBookingStatus, onEditBooking }: CalendarViewProps) {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedBooking, setSelectedBooking] = useState<BookingWithRelations | null>(null);
  const [showBookingDialog, setShowBookingDialog] = useState(false);

  const monthStart = startOfMonth(currentDate);
  const monthEnd = endOfMonth(currentDate);
  const calendarStart = startOfWeek(monthStart);
  const calendarEnd = endOfWeek(monthEnd);

  const calendarDays = eachDayOfInterval({
    start: calendarStart,
    end: calendarEnd
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'CONFIRMED':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'PENDING':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'CANCELLED':
        return 'bg-red-100 text-red-800 border-red-200';
      case 'DONE':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'CONFIRMED':
        return <CheckCircle className="w-3 h-3" />;
      case 'PENDING':
        return <AlertCircle className="w-3 h-3" />;
      case 'CANCELLED':
        return <XCircle className="w-3 h-3" />;
      case 'DONE':
        return <CheckCircle className="w-3 h-3" />;
      default:
        return <AlertCircle className="w-3 h-3" />;
    }
  };

  const getBookingsForDay = (day: Date) => {
    return bookings.filter(booking => 
      isSameDay(new Date(booking.dateTime), day)
    ).sort((a, b) => new Date(a.dateTime).getTime() - new Date(b.dateTime).getTime());
  };

  const handlePreviousMonth = () => {
    setCurrentDate(subMonths(currentDate, 1));
  };

  const handleNextMonth = () => {
    setCurrentDate(addMonths(currentDate, 1));
  };

  const handleBookingClick = (booking: BookingWithRelations) => {
    setSelectedBooking(booking);
    setShowBookingDialog(true);
  };

  const handleUpdateStatus = (status: string) => {
    if (selectedBooking && onUpdateBookingStatus) {
      onUpdateBookingStatus(selectedBooking.id, status);
      setShowBookingDialog(false);
    }
  };

  const handleEdit = () => {
    if (selectedBooking && onEditBooking) {
      onEditBooking(selectedBooking);
      setShowBookingDialog(false);
    }
  };

  return (
    <div className="space-y-6">
      {/* Calendar Header */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-2xl font-serif" data-testid="calendar-title">
              {format(currentDate, 'MMMM yyyy')}
            </CardTitle>
            <div className="flex items-center space-x-2">
              <Button
                variant="outline"
                size="sm"
                onClick={handlePreviousMonth}
                data-testid="button-previous-month"
              >
                <ChevronLeft className="w-4 h-4" />
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={handleNextMonth}
                data-testid="button-next-month"
              >
                <ChevronRight className="w-4 h-4" />
              </Button>
            </div>
          </div>
        </CardHeader>
      </Card>

      {/* Calendar Grid */}
      <Card>
        <CardContent className="p-0">
          <div className="grid grid-cols-7 border-b" data-testid="calendar-grid">
            {/* Day Headers */}
            {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((day, index) => (
              <div
                key={day}
                className="p-2 md:p-4 text-center font-medium text-muted-foreground border-r last:border-r-0 bg-muted/30 text-sm md:text-base"
                data-testid={`day-header-${day.toLowerCase()}`}
              >
                <span className="hidden sm:inline">{day}</span>
                <span className="sm:hidden">{day.charAt(0)}</span>
              </div>
            ))}

            {/* Calendar Days */}
            {calendarDays.map((day, dayIndex) => {
              const dayBookings = getBookingsForDay(day);
              const isCurrentMonth = isSameMonth(day, currentDate);
              const isToday = isSameDay(day, new Date());

              return (
                <div
                  key={dayIndex}
                  className={`min-h-[80px] sm:min-h-[100px] lg:min-h-[120px] p-1 sm:p-2 border-r border-b last:border-r-0 ${
                    isCurrentMonth 
                      ? 'bg-background' 
                      : 'bg-muted/20'
                  } ${
                    isToday 
                      ? 'bg-primary/5 border-primary/20' 
                      : ''
                  }`}
                  data-testid={`calendar-day-${format(day, 'yyyy-MM-dd')}`}
                >
                  <div className={`text-sm font-medium mb-2 ${
                    isCurrentMonth ? 'text-foreground' : 'text-muted-foreground'
                  } ${
                    isToday ? 'text-primary font-bold' : ''
                  }`}>
                    {format(day, 'd')}
                  </div>

                  <div className="space-y-1">
                    {dayBookings.slice(0, 2).map((booking) => (
                      <div
                        key={booking.id}
                        className={`p-1.5 sm:p-1 rounded-sm text-xs cursor-pointer hover:opacity-80 transition-opacity border ${getStatusColor(booking.status)} min-h-[44px] sm:min-h-auto touch-manipulation`}
                        onClick={() => handleBookingClick(booking)}
                        data-testid={`calendar-booking-${booking.id}`}
                      >
                        <div className="flex items-center space-x-1 mb-1">
                          {getStatusIcon(booking.status)}
                          <span className="font-medium truncate text-xs sm:text-xs">
                            {format(new Date(booking.dateTime), 'HH:mm')}
                          </span>
                        </div>
                        <div className="truncate font-medium text-xs">
                          {booking.service.name}
                        </div>
                        <div className="truncate text-xs opacity-75 hidden sm:block">
                          {booking.customerName || booking.user?.firstName || 'Guest'}
                        </div>
                      </div>
                    ))}

                    {dayBookings.length > 2 && (
                      <div 
                        className="text-xs text-muted-foreground text-center p-1 rounded cursor-pointer hover:bg-muted/20 transition-colors"
                        onClick={() => {
                          // Show the first remaining booking when clicking "+N more"
                          if (dayBookings.length > 2) {
                            handleBookingClick(dayBookings[2]);
                          }
                        }}
                        data-testid={`more-bookings-${format(day, 'yyyy-MM-dd')}`}
                      >
                        +{dayBookings.length - 2} more
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>

      {/* Booking Details Dialog */}
      <Dialog open={showBookingDialog} onOpenChange={setShowBookingDialog}>
        <DialogContent className="max-w-md mx-4" data-testid="booking-details-dialog">
          <DialogHeader>
            <DialogTitle>Booking Details</DialogTitle>
          </DialogHeader>

          {selectedBooking && (
            <div className="space-y-4">
              <div className="space-y-3">
                <div>
                  <h3 className="font-semibold text-lg" data-testid="dialog-service-name">
                    {selectedBooking.service.name}
                  </h3>
                  <p className="text-sm text-muted-foreground" data-testid="dialog-service-category">
                    {[
                      selectedBooking.service.isNail && "Nail",
                      selectedBooking.service.isEyelash && "Eyelash", 
                      selectedBooking.service.isFacial && "Facial"
                    ].filter(Boolean).join(", ") || "General"}
                  </p>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <div className="flex items-center space-x-2 text-sm">
                      <CalendarIcon className="w-4 h-4 text-muted-foreground" />
                      <span data-testid="dialog-booking-date">
                        {format(new Date(selectedBooking.dateTime), 'MMM dd, yyyy')}
                      </span>
                    </div>
                    <div className="flex items-center space-x-2 text-sm mt-1">
                      <Clock className="w-4 h-4 text-muted-foreground" />
                      <span data-testid="dialog-booking-time">
                        {format(new Date(selectedBooking.dateTime), 'h:mm a')} ({selectedBooking.durationMins}m)
                      </span>
                    </div>
                  </div>

                  <div>
                    <div className="flex items-center space-x-2 text-sm">
                      <User className="w-4 h-4 text-muted-foreground" />
                      <span data-testid="dialog-customer-name">
                        {selectedBooking.customerName || selectedBooking.user?.firstName || 'Guest'}
                      </span>
                    </div>
                    {selectedBooking.customerPhone && (
                      <div className="flex items-center space-x-2 text-sm mt-1">
                        <Phone className="w-4 h-4 text-muted-foreground" />
                        <span data-testid="dialog-customer-phone">
                          {selectedBooking.customerPhone}
                        </span>
                      </div>
                    )}
                    {selectedBooking.customerEmail && (
                      <div className="flex items-center space-x-2 text-sm mt-1">
                        <Mail className="w-4 h-4 text-muted-foreground" />
                        <span data-testid="dialog-customer-email">
                          {selectedBooking.customerEmail}
                        </span>
                      </div>
                    )}
                  </div>
                </div>

                <div className="flex items-center justify-between">
                  <Badge className={getStatusColor(selectedBooking.status)} data-testid="dialog-booking-status">
                    <span className="flex items-center space-x-1">
                      {getStatusIcon(selectedBooking.status)}
                      <span>{selectedBooking.status}</span>
                    </span>
                  </Badge>

                  {selectedBooking.staff && (
                    <div className="text-sm text-muted-foreground" data-testid="dialog-staff-name">
                      Staff: {selectedBooking.staff.name}
                    </div>
                  )}
                </div>

                {selectedBooking.notes && (
                  <div className="p-3 bg-muted/30 rounded text-sm" data-testid="dialog-booking-notes">
                    <strong>Notes:</strong> {selectedBooking.notes}
                  </div>
                )}
              </div>

              <div className="flex flex-wrap gap-2 pt-4 border-t">
                <Button 
                  size="sm" 
                  variant={selectedBooking.status === 'CONFIRMED' ? 'default' : 'outline'}
                  onClick={() => handleUpdateStatus('CONFIRMED')}
                  disabled={selectedBooking.status === 'CONFIRMED'}
                  data-testid="dialog-button-confirm"
                >
                  Confirm
                </Button>
                <Button 
                  size="sm" 
                  variant={selectedBooking.status === 'DONE' ? 'default' : 'outline'}
                  onClick={() => handleUpdateStatus('DONE')}
                  disabled={selectedBooking.status === 'DONE'}
                  data-testid="dialog-button-complete"
                >
                  Complete
                </Button>
                <Button 
                  size="sm" 
                  variant={selectedBooking.status === 'CANCELLED' ? 'destructive' : 'outline'}
                  onClick={() => handleUpdateStatus('CANCELLED')}
                  disabled={selectedBooking.status === 'CANCELLED'}
                  data-testid="dialog-button-cancel"
                >
                  Cancel
                </Button>
                {onEditBooking && (
                  <Button 
                    size="sm" 
                    variant="outline"
                    onClick={handleEdit}
                    data-testid="dialog-button-edit"
                  >
                    Edit
                  </Button>
                )}
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

