client/src/pages/admin/AdminLoginPage.ts.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Eye, EyeOff, Shield, Lock } from "lucide-react";

const adminLoginSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(1, "Password is required"),
});

type AdminLoginFormData = z.infer<typeof adminLoginSchema>;

export default function AdminLoginPage() {
  const [, setLocation] = useLocation();
  const auth = useAuth();
  const { user, adminLoginMutation } = auth;
  const [showPassword, setShowPassword] = useState(false);

  // Redirect if already logged in and is admin
  useEffect(() => {
    if (user) {
      if (user.role === 'ADMIN') {
        setLocation("/admin");
      } else {
        setLocation("/");
      }
    }
  }, [user, setLocation]);

  const form = useForm<AdminLoginFormData>({
    resolver: zodResolver(adminLoginSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  const onLogin = (data: AdminLoginFormData) => {
    adminLoginMutation.mutate(data, {
      onSuccess: (user) => {
        if (user.role === 'ADMIN') {
          setLocation("/admin");
        } else {
          // Not an admin, redirect to regular user area
          setLocation("/");
        }
      }
    });
  };

  if (user) {
    return null; // Will redirect via useEffect
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-800 flex items-center justify-center p-4">
      <Card className="w-full max-w-md shadow-2xl border-purple-200 dark:border-purple-800">
        <CardHeader className="text-center space-y-4">
          <div className="mx-auto bg-purple-100 dark:bg-purple-900 p-3 rounded-full w-16 h-16 flex items-center justify-center">
            <Shield className="h-8 w-8 text-purple-600 dark:text-purple-400" />
          </div>
          <div>
            <CardTitle className="text-2xl font-bold text-gray-900 dark:text-white">
              Admin Access
            </CardTitle>
            <CardDescription className="text-gray-600 dark:text-gray-300">
              HOME BASE Beauty Salon Management
            </CardDescription>
          </div>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onLogin)} className="space-y-4">
              <FormField
                control={form.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-gray-700 dark:text-gray-300">Admin Email</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="email"
                        placeholder="admin@homebase.beauty"
                        className="bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600"
                        data-testid="input-admin-email"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-gray-700 dark:text-gray-300">Password</FormLabel>
                    <FormControl>
                      <div className="relative">
                        <Input
                          {...field}
                          type={showPassword ? "text" : "password"}
                          placeholder="Enter admin password"
                          className="bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600 pr-10"
                          data-testid="input-admin-password"
                        />
                        <Button
                          type="button"
                          variant="ghost"
                          size="sm"
                          className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                          onClick={() => setShowPassword(!showPassword)}
                          data-testid="button-toggle-password"
                        >
                          {showPassword ? (
                            <EyeOff className="h-4 w-4 text-gray-400" />
                          ) : (
                            <Eye className="h-4 w-4 text-gray-400" />
                          )}
                        </Button>
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <Button
                type="submit"
                className="w-full bg-purple-600 hover:bg-purple-700 text-white font-medium py-2 px-4 rounded-md transition-colors duration-200"
                disabled={adminLoginMutation.isPending}
                data-testid="button-admin-login"
              >
                {adminLoginMutation.isPending ? (
                  <div className="flex items-center justify-center space-x-2">
                    <Lock className="h-4 w-4 animate-spin" />
                    <span>Authenticating...</span>
                  </div>
                ) : (
                  <div className="flex items-center justify-center space-x-2">
                    <Lock className="h-4 w-4" />
                    <span>Access Admin Panel</span>
                  </div>
                )}
              </Button>
            </form>
          </Form>

          <div className="mt-6 text-center">
            <p className="text-sm text-gray-600 dark:text-gray-400">
              Need customer access?{" "}
              <button
                onClick={() => setLocation("/auth")}
                className="text-purple-600 hover:text-purple-700 dark:text-purple-400 dark:hover:text-purple-300 font-medium"
                data-testid="link-customer-login"
              >
                Customer Login
              </button>
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

scripts/production-admin-setup.js.content:
#!/usr/bin/env node
/**
 * Production Admin Setup Script
 * Run this script to create admin account in production database
 * Usage: node scripts/production-admin-setup.js
 */

import { neon } from '@neondatabase/serverless';
import crypto from 'crypto';
import { promisify } from 'util';

const scryptAsync = promisify(crypto.scrypt);

async function hashPassword(password) {
  const salt = crypto.randomBytes(16).toString('hex');
  const buf = await scryptAsync(password, salt, 64);
  return `${buf.toString('hex')}.${salt}`;
}

async function setupProductionAdmin() {
  try {
    console.log('ðŸš€ Setting up admin account for production...');

    // Check if we're in production
    const isProduction = process.env.NODE_ENV === 'production' || process.env.REPLIT_DEPLOYMENT === '1';
    console.log(`ðŸ“ Environment: ${isProduction ? 'PRODUCTION' : 'DEVELOPMENT'}`);
    console.log(`ðŸ“Š Database URL: ${process.env.DATABASE_URL ? 'Connected' : 'Not found'}`);

    if (!process.env.DATABASE_URL) {
      throw new Error('DATABASE_URL environment variable not found');
    }

    const sql = neon(process.env.DATABASE_URL);

    // Create admin account with secure credentials
    const adminEmail = 'admin@homebase.beauty';
    const adminPassword = 'homebase2024';
    const hashedPassword = await hashPassword(adminPassword);

    // First, ensure sessions table exists (for production)
    try {
      await sql`
        CREATE TABLE IF NOT EXISTS sessions (
          sid VARCHAR PRIMARY KEY,
          sess JSON NOT NULL,
          expire TIMESTAMP NOT NULL
        )
      `;
      console.log('âœ… Sessions table ready');
    } catch (sessionError) {
      console.log('âš ï¸  Sessions table setup:', sessionError.message);
    }

    // Create or update admin account
    const result = await sql`
      INSERT INTO users (email, password, first_name, last_name, phone, role) 
      VALUES (
        ${adminEmail},
        ${hashedPassword},
        'Admin',
        'Manager', 
        '+84123456789',
        'ADMIN'
      ) ON CONFLICT (email) DO UPDATE SET
        password = EXCLUDED.password,
        role = 'ADMIN',
        first_name = EXCLUDED.first_name,
        last_name = EXCLUDED.last_name,
        phone = EXCLUDED.phone,
        updated_at = NOW()
      RETURNING email, role, created_at
    `;

    console.log('âœ… Admin account setup completed!');
    console.log('ðŸ“§ Email:', adminEmail);
    console.log('ðŸ”‘ Password:', adminPassword);
    console.log('ðŸ‘¤ Role:', result[0]?.role);
    console.log('ðŸ“… Created:', result[0]?.created_at);

    // Verify the account works
    const verification = await sql`
      SELECT email, role, first_name, last_name FROM users 
      WHERE email = ${adminEmail} AND role = 'ADMIN'
    `;

    if (verification.length > 0) {
      console.log('âœ… Verification successful - Admin account ready for production!');
      console.log('ðŸŒ You can now login at /admin/login on your published website');
    } else {
      console.log('âŒ Verification failed - Admin account not found');
    }

  } catch (error) {
    console.error('âŒ Error setting up production admin:', error.message);
    console.error('ðŸ’¡ Make sure your DATABASE_URL environment variable is correctly set');
    process.exit(1);
  }
}

// Run the setup
setupProductionAdmin();
